import React, {useEffect, useState} from 'react';
import './styles/App.css';
import {getUsers} from "./requestData";

function App() {

    const [users, setUsers] = useState([])

    useEffect( () => {
        const fetchData = async () => {
            setUsers(await getUsers())
        }
        fetchData()
    }, [])

    if (users && users.length) {
        return (
            <div className="app">
                {users.map(el => (
                        <div key={el.id}> {el.firstName}</div>
                    ))
                }
            </div>
        )
    } else {
        return (
            <h1>Data is empty</h1>
        )
    }
}

export default App;
