import React, {useEffect, useState} from 'react';
import './styles/App.scss';
import {getUsers} from "./api/requestData";
import {UsersListComponent} from "./components/usersListComponent/UsersListComponent";
import {NavLink, Route} from "react-router-dom";
import {Spinner} from './components/Spinner/Spinner'
import {Home} from "./components/Home/Home";


export const App = () => {

    const [usersData, setUsersData] = useState([])

    useEffect( () => {
        const fetchData = async () => {
            setUsersData(await getUsers())
        }
        fetchData()
    }, [])

    return (
        <>
            <div className="navigation">
                <NavLink exact activeClassName="navigation__active-link" className="navigation__link" to="/">
                    Home
                </NavLink>
                <NavLink activeClassName="navigation__active-link" className="navigation__link" to="/users">
                    Users
                </NavLink>
            </div>
            <div>
                <Route exact path="/" render={() => <Home />} />
                <Route exact path="/users" render={() => {
                    if (usersData && usersData.length) {
                        return <UsersListComponent usersData={usersData}/>
                    } else {
                        return <Spinner />
                    }
                }} />
            </div>
        </>
    )
}
