import React from 'react'
import classnames from 'classnames'

export const UsersList = ({usersData}) => {
    const getDob = (dob) => ({
        month: (new Date(dob)).getDate(),
        month: (new Date(dob)).getMonth(),
        year: (new Date(dob)).getFullYear(),
    })

    // const colorDateByDob = (dob) => {
    //     if (((new Date(dob)).getMonth()) >= 0 && ((new Date(dob)).getMonth()) <= 2) {
    //         return true
    //     }
    // }

    return (
        <div className="list-users">
            {usersData.map(el => (
                <div key={el.id} className="list-users__wrapper">
                    <p className="list-users__item">{el.firstName}</p>
                    <p className="list-users__item">{el.lastName}</p>
                    <p
                        className={classnames({
                            "list-users__item": true,
                            "list-users__dob--gray": getDob(el.dob).month >= 0 && getDob(el.dob).month <= 2,
                            "list-users__dob--blue": getDob(el.dob).month >= 3 && getDob(el.dob).month <= 6,
                            "list-users__dob--green": getDob(el.dob).month >= 7 && getDob(el.dob).month <= 10,
                            "list-users__dob--red": getDob(el.dob).month >= 11,
                        })}
                    >
                        {`${getDob(el.dob).day}-${getDob(el.dob).month}-${getDob(el.dob).year}`}
                    </p>
                </div>
            ))
            }
        </div>
    )
}
