import React, {useEffect, useState} from 'react';
import './styles/App.css';
import {getUsers} from "./requestData";

function App() {

    const [users, setUsers] = useState([])

    useEffect( () => {
        const fetchData = async () => {
            setUsers(await getUsers())
        }
    }, [])

  return (
    <div className="app">
        {
            users && users.length
                ? users.map(el => (
                    <div key={el.id}> {el.firstName}</div>
                ))
                : null
        }
    </div>
  );
}

export default App;
